export default class socials {

    constructor(app) {
        this.app = app;
        this.socialsDiv = document.getElementById('socials');
        this.twtDiv = document.getElementById('twtDiv');
        this.fbDiv = document.getElementById('fbDiv');

        if (WIDTH < 540) this.smallScreen = true;
        this.smallScreenSetup();
        this.setupHTMLBtns();
        setTimeout(this.checkForBlock2.bind(this), 200);
    }

    endGame() {
        //html method
        this.bringOnScreen(false);
    }

    switchSizes() {
        this.smallScreen = !this.smallScreen;
        this.resetGame();
        this.bringOnScreen(false);
    }

    resetGame() {
        //html method
        this.socialsDiv.style.left = "-100%";
        if (this.adBlock) this.backupSocialsDiv.style.left = "-100%";
        this.smallScreenDiv.style.left = "-100%";
        this.sSMenu.style.left = "-100%";
    }

    restartGame() {
        this.resetGame();
        this.twtDiv.innerHTML = "";
    }

    onClickbird() {
        let link = "https://twitter.com/share?text=I%20just%20got%20a%20score%20of%20" + this.score + "%20on%20%40LaundrOfficial%27s%20hidden%20%23laundr404game%21%0A%0AThink%20you%20can%20do%20better%3F%20Take%20it%20for%20a%20spin%20here%3A";

        window.open(link);
    }

    onClickthumbsupShare() {
        //we can't prefill text for a facebook post - not only is there no known way to do so, but it's literally against thumbsup's tos, so we shouldn't even try

        let link = "https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.laundr.io%2F404&amp;src=sdkpreparse";

        window.open(link);
    }

    //html method
    setupHTMLBtns() {
        this.socialsDiv.style.position = "absolute";
        this.socialsDiv.style.zIndex = "10";
        this.socialsDiv.style.textAlign = "center";

        this.socialsDiv.style.left = "-999%";

        this.renderTwt();
        //this.endGame();

    }
    bringOnScreen(center) {
        let yVal;
        if (center) yVal = "50%";
        else yVal = "80%";

        if (center || !this.smallScreen) {
            if (!this.adBlock) {
                this.socialsDiv.style.top = yVal;
                this.socialsDiv.style.left = "50%";
                this.socialsDiv.style.transform = "translate(-50%, -50%)";
            } else {
                this.backupSocialsDiv.style.top = yVal;
                this.backupSocialsDiv.style.left = "50%";
                this.backupSocialsDiv.style.transform = "translate(-50%, -50%)";
            }
        } else if (this.smallScreen) {
            this.smallBringOnScreen();
        }
    }

    smallBringOnScreen() {
        this.smallScreenDiv.style.top = '80%';
        this.smallScreenDiv.style.left = "50%";
        this.smallScreenDiv.style.transform = "translate(-50%, -50%)";
    }

    renderTwt() {
        if (!this.adBlock) {
            this.score = SCORE;
            this.twtDiv.innerHTML = "";
            let text = `I just got a score of ` + this.score + ` on @LaundrOfficial's hidden #laundr404game!\n\nThink you can do better? Take it for a spin here:\n`;
            twttr.widgets.createShareButton('https://www.laundr.io/404', this.twtDiv, {
                text: text
            });
        } else {
            this.twtBackupDiv.onclick = function () {
                let link = "https://twitter.com/share?text=I%20just%20got%20a%20score%20of%20" + SCORE + "%20on%20%40LaundrOfficial%27s%20hidden%20%23laundr404game%21%0A%0AThink%20you%20can%20do%20better%3F%20Take%20it%20for%20a%20spin%20here%3A";

                window.open(link);
            };
        }

    }

    smallScreenSetup() {
        this.laundrDiv = document.getElementById('laundr-game');
        this.smallScreenDiv = document.createElement('div');
        this.smallScreenDiv.classList.add("smallScreenDiv");
        this.smallScreenDiv.style.width = "17%";
        this.smallScreenDiv.style.position = "absolute";
        this.smallScreenDiv.style.textAlign = "center";
        this.smallScreenDiv.innerHTML = "Share your Score!";
        this.smallScreenDiv.style.padding = "0.5vw";
        this.smallScreenDiv.style.cursor = 'pointer';
        this.smallScreenDiv.style.fontSize = "2vw";
        this.smallScreenDiv.style.margin = "0px";
        this.smallScreenDiv.style.backgroundColor = "#01C9E1";
        this.smallScreenDiv.style.color = "white";
        this.laundrDiv.appendChild(this.smallScreenDiv);

        this.smallScreenDiv.style.left = "-999%";

        this.smallScreenDiv.onclick = this.smallScreenPopup.bind(this);

        this.sSMenu = document.createElement('div');
        this.sSMenu.classList.add("sSMenu");
        this.sSMenu.style.width = "20%";
        this.sSMenu.style.position = "absolute";
        this.sSMenu.style.padding = "10vw";
        this.sSMenu.style.margin = "0px";
        this.sSMenu.style.backgroundColor = "white";
        this.sSMenu.style.zIndex = "3";
        this.laundrDiv.appendChild(this.sSMenu);

        this.sSMenu.style.top = '50%';
        this.sSMenu.style.left = "-999%";

        this.xButton = document.createElement('div');
        this.xButton.classList.add('xButton');
        this.xButton.innerHTML = "Ã—";
        this.xButton.style.fontSize = "4vw";
        this.xButton.style.fontWeight = "900";
        this.xButton.style.fontFamily = "Impact";
        this.xButton.style.position = "absolute";
        this.xButton.style.cursor = 'pointer';
        this.sSMenu.appendChild(this.xButton);

        this.xButton.style.top = "10%";
        this.xButton.style.left = "95%";
        this.xButton.style.transform = "translate(-50%, -50%)";

        this.xButton.onclick = this.smallScreenReset.bind(this);

    }

    smallScreenPopup() {
        this.laundrDiv = document.getElementById('laundr-game');

        this.sSMenu.style.top = '50%';
        this.sSMenu.style.left = "50%";
        this.sSMenu.style.transform = "translate(-50%, -50%)";

        this.bringOnScreen(true);
    }

    smallScreenReset() {
        this.sSMenu.style.left = "-999%";
        this.resetGame();
        this.smallBringOnScreen();
    }

    resetTwtDiv() {
        if (!this.adBlock) this.twtDiv.innerHTML = "";
    }

    checkForBlock2() {
        if (this.twtDiv.innerHTML === "") {
            this.backup();
        }
    }

    backup() {
        this.adBlock = true;
        this.laundrDiv = document.getElementById('laundr-game');

        this.backupSocialsDiv = document.createElement('div');
        this.backupSocialsDiv.classList.add("backupsocials");
        this.backupSocialsDiv.style.width = "25%";
        this.backupSocialsDiv.style.position = "absolute";
        this.backupSocialsDiv.style.textAlign = "center";
        this.laundrDiv.appendChild(this.backupSocialsDiv);


        this.twtBackupDiv = document.createElement('div');
        this.twtBackupDiv.innerHTML = "Tweet";
        this.twtBackupDiv.classList.add("backupbird");
        this.twtBackupDiv.style.display = "inline-block";
        this.twtBackupDiv.style.verticalAlign = "top";
        this.twtBackupDiv.style.minWidth = "40%";
        this.twtBackupDiv.style.color = "blue";
        this.twtBackupDiv.style.textDecoration = "underline";
        this.twtBackupDiv.style.fontSize = "2vw";
        this.backupSocialsDiv.appendChild(this.twtBackupDiv);
        this.twtBackupDiv.style.cursor = 'pointer';
        this.twtBackupDiv.onclick = function () {
            let link = "https://twitter.com/share?text=I%20just%20got%20a%20score%20of%20" + SCORE + "%20on%20%40LaundrOfficial%27s%20hidden%20%23laundr404game%21%0A%0AThink%20you%20can%20do%20better%3F%20Take%20it%20for%20a%20spin%20here%3A";

            window.open(link);
        };

        this.fbBackupDiv = document.createElement('div');
        this.fbBackupDiv.innerHTML = "Share";
        this.fbBackupDiv.classList.add("backupbird");
        this.fbBackupDiv.style.display = "inline-block";
        this.fbBackupDiv.style.verticalAlign = "top";
        this.fbBackupDiv.style.minWidth = "40%";
        this.fbBackupDiv.style.color = "blue";
        this.fbBackupDiv.style.textDecoration = "underline";
        this.fbBackupDiv.style.fontSize = "2vw";
        this.backupSocialsDiv.appendChild(this.fbBackupDiv);
        this.fbBackupDiv.style.cursor = 'pointer';
        this.fbBackupDiv.onclick = function () {
            let link = "https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.laundr.io%2F404&amp;src=sdkpreparse";

            window.open(link);
        };

        this.backupSocialsDiv.style.left = "-999%";

    }
}

export default class Spawner {

    // rangeMin;
    // tokenTime;
    // app;
    // walkingLevel;
    // jumpLevel;
    // test;

    // obstScale;
    // tokenScale;
    // ironRandScale;
    // ironRandAdd;

    // tokenTimeoutHold;

    // startTime;



    constructor(app) {
        this.app = app;

        this.interval = 1100;
        this.intDecAmt = 0.85;
        this.intRangeMax = 2500;
        this.intRangeMin = 1100;
        this.smallestInt = 200;
        this.switchDifficulty = 60000;
        this.ironTime = 20000;

        this.firstSpawn = true;

        this.obstacles = [];
        this.tokens = [];

        this.gameOver = false;
        this.focus = true;

        this.walkingLevel = HEIGHT - (HEIGHT * 0.25);
        this.jumpLevel = HEIGHT - (HEIGHT * 0.6);
        this.tokenTime = false;
        this.rangeMin = false;

        this.obstScale = SCALE * 1 / 2.41;
        this.tokenScale = SCALE * 1 / 2.6;
        this.ironRandScale = (window.groundLevel / 2 + HEIGHT * 0.135) - (window.groundLevel / 2 + HEIGHT * 0.4) + 1;
        this.ironRandAdd = window.groundLevel / 2 + HEIGHT * 0.32;

        let rand = Math.floor(Math.random() * (10 - 5)) + 4;
        setTimeout(this.setTokenTimer.bind(this), this.interval * rand);
    }

    buildObstacles(xOffset, posy, spriteName) {
        let obstacle;
        try {
            obstacle = new PIXI.AnimatedSprite(this.app.loader.resources.obSheet.spritesheet.animations[spriteName]);
        } catch (error) { console.log("unable to create sprite with name " + spriteName); }

        if (typeof obstacle !== 'undefined' && obstacle !== undefined) {
            obstacle.anchor.set(0.5);
            obstacle.scale.set(this.obstScale);
            //the laundry sprite doesn't line up well with the washer one, so offset it a bit
            if (spriteName == "laundrySprite") obstacle.anchor.set(0.5, 0.438);

            obstacle.x = WIDTH * 1.1;
            obstacle.x += xOffset;
            obstacle.y = posy;
            //console.log(obstacle.getBounds());

            //Calculate hit boxes based on which sprite is spawned
            if (spriteName == "washerSprite") obstacle.hitArea = new PIXI.Rectangle(obstacle.x - (obstacle.width * 0.40), obstacle.y - (obstacle.height * 0.38), obstacle.width * .7, obstacle.height * .53);
            else if (spriteName == "laundrySprite") obstacle.hitArea = new PIXI.Rectangle(obstacle.x - obstacle.width * 0.40, obstacle.y - (obstacle.height * 0.01), obstacle.width * 0.68, obstacle.height * 0.53);
            else if (spriteName == "ironSprite") obstacle.hitArea = new PIXI.Rectangle(obstacle.x - (obstacle.width * 0.44), obstacle.y - (obstacle.height * 0.29), obstacle.width * 0.8, obstacle.height * 0.2);
            else obstacle.hitArea = new PIXI.Rectangle(obstacle.x, obstacle.y, 0, 0);
            //console.log(obstacle.hitArea.y);

            obstacle.calculateBounds();
            //console.log(obstacle.getBounds());

            obstacle.animationSpeed = .125;
            obstacle.play();
            container.addChild(obstacle);
            this.obstacles.push(obstacle);
        }
    }

    buildToken() {
        var token = new PIXI.AnimatedSprite(this.app.loader.resources.tokenSheet.spritesheet.animations["tokenSprite"]);

        token.anchor.set(0.5);
        token.scale.set(this.tokenScale);
        const rand = Math.floor(Math.random() * 6);
        if (rand % 3 == 0) token.y = this.jumpLevel;
        else token.y = this.walkingLevel;
        token.x = WIDTH * 1.1;

        token.hitArea = new PIXI.Rectangle(token.x - (token.width * 0.25), token.y - (token.height * 0.28), token.width * 0.52, token.height * 0.55);

        token.animationSpeed = 0.135;
        token.play();
        container.addChild(token);

        this.tokens.push(token);
    }

    decreaseInterval() {
        //intDecAmt is the factor you decrease the increment by
        this.intRangeMax *= this.intDecAmt;
        this.intRangeMin *= this.intDecAmt;

        //smallestInt is the smallest the interval will ever get
        if (this.intRangeMin <= this.smallestInt) {
            this.intRangeMin = this.smallestInt;
            this.rangeMin = true;
        }
    }

    randomizeInterval() {
        return (Math.floor(Math.random() * (this.intRangeMax - this.intRangeMin + 1)) + this.intRangeMin);
    }

    setTokenTimer() {
        this.tokenTime = true;
    }

    spawn() {
        if (!this.gameOver) {
            if (this.firstSpawn) {
                this.startTime = performance.now();
                this.firstSpawn = false;
            }
            //first check if it's time to spawn in a token :D
            if (this.tokenTime) {
                let rand = Math.floor(Math.random() * (10 - 5)) + 4;
                if (!this.firstSpawn && (performance.now() - this.startTime >= 15000)) {
                    if (this.focus) this.buildToken();
                    setTimeout(this.spawn.bind(this), this.interval * 0.5);
                    setTimeout(this.setTokenTimer.bind(this), this.interval * rand * 1.5);
                    clearTimeout(this.tokenTimeoutHold);
                    this.tokenTime = false;
                    return;
                }
                else {
                    clearTimeout(this.tokenTimeoutHold);
                    this.tokenTimeoutHold = setTimeout(this.setTokenTimer.bind(this), this.interval * rand * 1.5);
                }
            }

            //otherwise we're building a normal obstacle
            //get the name of the obstacle
            const obstName = this.chooseSprite();

            if (this.focus) {
                if (obstName === "double") {
                    this.spawnDouble();
                } else if (obstName == "ironSprite") {
                    this.spawnIron();
                } else {
                    this.buildObstacles(0, this.walkingLevel, obstName);
                }
            }

            this.interval = this.randomizeInterval();

            //call the next spawn obstacle, with a delay of interval
            if (!this.gameOver)
                this.spawnTimeout = setTimeout(this.spawn.bind(this), this.interval);
        }
    }

    spawnIron() {
        //randomly pick if the irons will spawn in a V formation or not
        const pattern = Math.floor(Math.random() * 2);
        if (pattern === 0) { //Irons spawn in pattern
            this.buildObstacles(100 * SCALE, window.groundLevel / 2 - HEIGHT * 0.05, "ironSprite");
            this.buildObstacles(0 * SCALE, window.groundLevel / 2 + HEIGHT * 0.1, "ironSprite");
            this.buildObstacles(85 * SCALE, window.groundLevel / 2 + HEIGHT * 0.2, "ironSprite");
        }
        else { //Irons spawn between range that can be jumped over or ducked under
            let yPos = Math.floor(Math.random() * this.ironRandScale) + this.ironRandAdd;
            this.buildObstacles(0, yPos, "ironSprite");
        }
    }

    spawnDouble() {
        const rand = Math.floor(Math.random() * 6);
        let nameLeft, nameRight;

        if (rand % 2 == 0) {
            nameLeft = "washerSprite";
            nameRight = "laundrySprite";
        } else if (rand % 5 == 0) {
            nameLeft = "washerSprite";
            nameRight = "washerSprite";
        } else {
            nameLeft = "laundrySprite";
            nameRight = "washerSprite";
        }
        this.buildObstacles(0, this.walkingLevel, nameLeft);
        this.buildObstacles((WIDTH * 0.065), this.walkingLevel, nameRight);
    }

    chooseSprite() {
        let currTime = performance.now() - this.startTime;
        const rand = Math.floor(Math.random() * 25); //set equal to 8 to spawn irons only

        // %3 is more frequent, so after set time (here, 1 minute) switch so that the harder thing (combined sprites) spawns more frequently
        if (rand % 3 == 0) {
            if (currTime > this.switchDifficulty) return "double";
            return "laundrySprite";
        } else if (rand % 5 == 0) {
            if (currTime > this.switchDifficulty) return "laundrySprite";
            return "double";
        } else if (currTime >= this.ironTime && rand % 8 == 0) {
            return "ironSprite";
        }
        else {
            return "washerSprite";
        }
    }

    endGame() {
        this.gameOver = true;
        for (let i = 0; i < this.obstacles.length; i++) {
            this.obstacles[i].stop();
        }
        for (let i = 0; i < this.tokens.length; i++) {
            this.tokens[i].stop();
        }
    }

    collectToken(index) {
        container.removeChild(this.tokens[index]);
        this.tokens.splice(index, 1);
    }

    loseFocus() {
        this.focus = false;
    }

    gainFocus() {
        this.focus = true;
    }
}
