export default class Player {
    // groundLevel;

    // app;




    // currSprite;
    // running;
    // jumping;
    // jumpStatic;
    // ducking;
    // falling;

    // jumpS;

    constructor(app, jumpS) {
        this.app = app;

        this.groundLevel = HEIGHT - (HEIGHT * .1);

        this.speedY = 0;
        this.gameOver = false;
        this.loaded = false;
        this.needsFall = false;
        this.fallComplete = false;

        this.jumpS = jumpS;

        //set up all the sprites
        this.createSprites();
    }

    updateJump() {
        // If the player is on the ground, not ducking, and trying to jump, start the jump sequence with an initial jump speed
        if (this.currSprite.y == this.groundLevel && !window.inputs.duck && window.inputs.jump) {
            if (!window.mute) this.jumpS.play();
            this.speedY = 3.5;
            this.switchSprite(this.jumpStatic);
            // this.switchSprite(this.jumping);
        }

        // If player is in air, add gravity
        else if (this.currSprite.y < this.groundLevel) {
            this.speedY -= .12 * FPSSCALE;
        }

        // If player is rising and holding jump, keep them up longer
        if (this.speedY > 0 && window.inputs.jump) {
            this.speedY += .06 * FPSSCALE;
        }

        // Once the jump animation is completed, switch to static animation
        // if (this.currSprite === this.jumping && this.currSprite.currentFrame == this.jumping.size - 1){
        //     this.switchSprite(this.jumpStatic);
        // }

        this.currSprite.y -= SCALE * this.speedY * FPSSCALE;
        this.currSprite.hitArea.y = this.currSprite.y;

        if (this.currSprite.y > this.groundLevel) {
            this.speedY = 0;
            this.currSprite.y = this.groundLevel;
            this.currSprite.hitArea.y = this.groundLevel;
            this.switchSprite(this.running);
        }
    }

    updateDuck() {
        if (window.inputs.duck) {
            // If ducking on (or below) ground, use ducking sprite
            if (this.currSprite.y >= this.groundLevel)
                this.switchSprite(this.ducking);

            // If ducking in midair, move player down faster
            else
                this.speedY -= .15;
        }

        // End of duck
        else if (this.currSprite === this.ducking) {
            this.switchSprite(this.running);
        }
    }

    reset() {
        this.currSprite.x = WIDTH * 0.22;
        this.currSprite.y = HEIGHT - (HEIGHT * .1);
        this.switchSprite(this.running);
        this.currSprite.x = WIDTH * 0.22;
        this.currSprite.y = HEIGHT - (HEIGHT * .1);
        this.currSprite.hitArea.y = WIDTH * 0.22;
    }

    switchSprite(sprite) {
        // Only switch sprite if necesary
        if (this.currSprite !== sprite) {
            let y = this.currSprite.y;
            if (sprite === this.falling) y += WIDTH * 0.0016;
            else if (this.currSprite === this.falling) y -= WIDTH * 0.0016;

            container.removeChild(this.currSprite);
            this.currSprite = sprite;
            this.currSprite.hitArea = sprite.hitArea;
            this.currSprite.y = y;
            this.currSprite.hitArea.y = y;
            container.addChild(this.currSprite);
        }
    }

    endGame(win) {
        if (!win) {
            if (this.currSprite.y != this.groundLevel) {
                let hold = this.currSprite.y;
                this.switchSprite(this.falling);
                this.currSprite.y = hold;
                this.falling.gotoAndPlay(0);
                this.needsFall = true;
            }
            else {
                this.switchSprite(this.falling);
                this.falling.gotoAndPlay(0);
            }
        } else {
            this.winSequence = true;
            setTimeout(this.sitDown.bind(this), 700);
        }
    }

    sitDown() {
        this.winSequence = false;
        this.ducking.stop();
        let x = this.currSprite.x;
        this.switchSprite(this.ducking);
        this.currSprite.x = x;
    }

    endGameFall() {
        if (this.currSprite.y < this.groundLevel + WIDTH * 0.0016) {
            this.currSprite.y += SCALE * 4;
        } else {
            this.currSprite.y = this.groundLevel + WIDTH * 0.0016;
            this.fallComplete = true;
        }
    }

    createSprites() {
        //only call this the one time in the construtor!!
        let height = SCALE / 1.7;

        let spriteWidth = -(WIDTH - (WIDTH * 0.22)) / 9.8;
        let spriteHeight = -(WIDTH - (WIDTH * 0.25)) / 10.5;
        let spriteSmallHeight = -(WIDTH - (WIDTH * 0.35)) / 11.5;
        let widthOffset = WIDTH * 0.005;
        let heightOffset = spriteHeight - spriteSmallHeight;

        this.running = new PIXI.AnimatedSprite(this.app.loader.resources.charaSheet.spritesheet.animations["running_WithSock"]);
        this.running.scale.set(height);
        this.running.interactive = true;
        this.running.x = WIDTH * 0.22;
        this.running.y = HEIGHT - (HEIGHT * .1);
        this.running.hitArea = new PIXI.Rectangle(this.running.x - widthOffset, this.running.y + heightOffset, spriteWidth, spriteSmallHeight);
        //console.log(this.running.hitArea.width);
        this.running.animationSpeed = .15;
        this.running.play();
        this.running.zIndex = 3;


        this.jumping = new PIXI.AnimatedSprite(this.app.loader.resources.charaSheet.spritesheet.animations["jumping_WithSock"]);
        this.jumping.scale.set(height);
        this.jumping.interactive = true;
        this.jumping.x = WIDTH * 0.22;
        this.jumping.y = HEIGHT - (HEIGHT * .1);
        this.jumping.hitArea = new PIXI.Rectangle(this.jumping.x, this.jumping.y, spriteWidth, spriteHeight);
        this.jumping.animationSpeed = .15;
        this.jumping.play();
        this.jumping.zIndex = 3;

        this.jumpStatic = new PIXI.Sprite(this.app.loader.resources.charaSheet.spritesheet.textures["jumping_WithSock_1.png"]);
        this.jumpStatic.scale.set(height);
        this.jumpStatic.interactive = true;
        this.jumpStatic.x = WIDTH * 0.22;
        this.jumpStatic.y = HEIGHT - (HEIGHT * .1);
        this.jumpStatic.hitArea = new PIXI.Rectangle(this.jumpStatic.x - widthOffset, this.jumpStatic.y + heightOffset, spriteWidth, spriteHeight);
        this.jumpStatic.zIndex = 3;

        this.ducking = new PIXI.AnimatedSprite(this.app.loader.resources.charaSheet.spritesheet.animations["duck_WithSock"]);
        this.ducking.scale.set(height);
        this.ducking.interactive = true;
        this.ducking.x = WIDTH * 0.22;
        this.ducking.y = HEIGHT - (HEIGHT * .1);
        this.ducking.hitArea = new PIXI.Rectangle(this.ducking.x, this.ducking.y, spriteWidth, spriteHeight * 0.68);
        this.ducking.animationSpeed = .15;
        this.ducking.play();
        this.ducking.zIndex = 3;

        this.falling = new PIXI.AnimatedSprite(this.app.loader.resources.charaSheet.spritesheet.animations["falling_WithSock"]);
        this.falling.scale.set(height);
        this.falling.interactive = true;
        this.falling.x = WIDTH * 0.22;
        this.falling.y = HEIGHT - (HEIGHT * .1) + WIDTH * 0.0016;
        this.falling.hitArea = new PIXI.Rectangle(this.falling.x, this.falling.y, spriteWidth, spriteHeight);
        this.falling.animationSpeed = .25;
        this.falling.loop = false;
        this.falling.zIndex = 3;

        this.currSprite = this.ducking;
        this.currSprite.hitArea = this.ducking.hitArea;
        container.addChild(this.currSprite);
        this.loaded = true;

    }
}

export default class socials {

    constructor(app) {
        this.app = app;
        this.socialsDiv = document.getElementById('socials');
        this.twtDiv = document.getElementById('twtDiv');
        this.fbDiv = document.getElementById('fbDiv');

        if (WIDTH < 540) this.smallScreen = true;
        this.smallScreenSetup();
        this.setupHTMLBtns();
        setTimeout(this.checkForBlock2.bind(this), 200);
    }

    endGame() {
        //html method
        this.bringOnScreen(false);
        this.renderTwt();
    }

    switchSizes() {
        this.smallScreen = !this.smallScreen;
        this.resetGame();
        this.bringOnScreen(false);
    }

    resetGame() {
        //html method
        this.socialsDiv.style.left = "-100%";
        if (this.adBlock) this.backupSocialsDiv.style.left = "-100%";
        this.smallScreenDiv.style.left = "-100%";
        this.sSMenu.style.left = "-100%";
    }

    restartGame() {
        this.resetGame();
        this.twtDiv.innerHTML = "";
    }

    onClickbird() {
        let link = "https://twitter.com/share?text=I%20just%20got%20a%20score%20of%20" + this.score + "%20on%20%40LaundrOfficial%27s%20hidden%20%23laundr404game%21%0A%0AThink%20you%20can%20do%20better%3F%20Take%20it%20for%20a%20spin%20here%3A";

        window.open(link);
    }

    onClickthumbsupShare() {
        //we can't prefill text for a facebook post - not only is there no known way to do so, but it's literally against thumbsup's tos, so we shouldn't even try

        let link = "https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.laundr.io%2F404&amp;src=sdkpreparse";

        window.open(link);
    }

    //html method
    setupHTMLBtns() {
        this.socialsDiv.style.position = "absolute";
        this.socialsDiv.style.zIndex = "10";
        this.socialsDiv.style.textAlign = "center";

        this.socialsDiv.style.left = "-999%";

        this.renderTwt();
        //this.endGame();

    }
    bringOnScreen(center) {
        let yVal;
        if (center) yVal = "50%";
        else yVal = "78%";

        if (center || !this.smallScreen) {
            if (!this.adBlock) {
                this.socialsDiv.style.top = yVal;
                this.socialsDiv.style.left = "50%";
                this.socialsDiv.style.transform = "translate(-50%, -50%)";
            } else {
                this.backupSocialsDiv.style.top = yVal;
                this.backupSocialsDiv.style.left = "50%";
                this.backupSocialsDiv.style.transform = "translate(-50%, -50%)";
            }
        } else if (this.smallScreen) {
            this.smallBringOnScreen();
        }
    }

    smallBringOnScreen() {
        this.smallScreenDiv.style.top = '78%';
        this.smallScreenDiv.style.left = "50%";
        this.smallScreenDiv.style.transform = "translate(-50%, -50%)";
    }

    renderTwt() {
        if (!this.adBlock) {
            this.score = SCORE;
            this.twtDiv.innerHTML = "";
            let text = `I just got a score of ` + this.score + ` on @LaundrOfficial's hidden #laundr404game!\n\nThink you can do better? Take it for a spin here:\n`;
            twttr.widgets.createShareButton('https://www.laundr.io/404', this.twtDiv, {
                text: text
            });
        } else {
            this.twtBackupDiv.onclick = function () {
                let link = "https://twitter.com/share?text=I%20just%20got%20a%20score%20of%20" + SCORE + "%20on%20%40LaundrOfficial%27s%20hidden%20%23laundr404game%21%0A%0AThink%20you%20can%20do%20better%3F%20Take%20it%20for%20a%20spin%20here%3A";

                window.open(link);
            };
        }

    }

    smallScreenSetup() {
        this.laundrDiv = document.getElementById('laundr-game');
        this.smallScreenDiv = document.createElement('div');
        this.smallScreenDiv.classList.add("smallScreenDiv");
        this.smallScreenDiv.style.width = "17%";
        this.smallScreenDiv.style.position = "absolute";
        this.smallScreenDiv.style.textAlign = "center";
        this.smallScreenDiv.innerHTML = "Share your Score!";
        this.smallScreenDiv.style.padding = "0.5vw";
        this.smallScreenDiv.style.cursor = 'pointer';
        this.smallScreenDiv.style.fontSize = "2vw";
        this.smallScreenDiv.style.margin = "0px";
        this.smallScreenDiv.style.backgroundColor = "#01C9E1";
        this.smallScreenDiv.style.color = "white";
        this.laundrDiv.appendChild(this.smallScreenDiv);

        this.smallScreenDiv.style.left = "-999%";

        this.smallScreenDiv.onclick = this.smallScreenPopup.bind(this);

        this.sSMenu = document.createElement('div');
        this.sSMenu.classList.add("sSMenu");
        this.sSMenu.style.width = "5%";
        this.sSMenu.style.position = "absolute";
        this.sSMenu.style.padding = "10vw";
        this.sSMenu.style.margin = "0px";
        this.sSMenu.style.backgroundColor = "#F9F9F9";
        this.sSMenu.style.zIndex = "3";
        this.laundrDiv.appendChild(this.sSMenu);

        this.sSMenu.style.top = '50%';
        this.sSMenu.style.left = "-999%";

        this.xButton = document.createElement('div');
        this.xButton.classList.add('xButton');
        this.xButton.innerHTML = "";
        this.xButton.style.fontSize = "4vw";
        this.xButton.style.fontWeight = "900";
        this.xButton.style.fontFamily = "Impact";
        this.xButton.style.position = "absolute";
        this.xButton.style.cursor = 'pointer';
        this.sSMenu.appendChild(this.xButton);

        this.xButton.style.top = "10%";
        this.xButton.style.left = "95%";
        this.xButton.style.transform = "translate(-50%, -50%)";

        this.xButton.onclick = this.smallScreenReset.bind(this);

    }

    smallScreenPopup() {
        this.laundrDiv = document.getElementById('laundr-game');

        this.sSMenu.style.top = '48%';
        this.sSMenu.style.left = "50%";
        this.sSMenu.style.transform = "translate(-50%, -50%)";

        this.bringOnScreen(true);
    }

    smallScreenReset() {
        this.sSMenu.style.left = "-999%";
        this.resetGame();
        this.smallBringOnScreen();
    }

    resetTwtDiv() {
        if (!this.adBlock) this.twtDiv.innerHTML = "";
    }

    checkForBlock2() {
        if (this.twtDiv.innerHTML === "") {
            this.backup();
        }
    }

    backup() {
        this.adBlock = true;

        this.laundrDiv = document.getElementById('laundr-game');

        this.backupSocialsDiv = document.createElement('div');
        this.backupSocialsDiv.classList.add("backupsocials");
        this.backupSocialsDiv.style.width = "25%";
        this.backupSocialsDiv.style.position = "absolute";
        this.backupSocialsDiv.style.zIndex = "5";
        this.backupSocialsDiv.style.textAlign = "center";
        this.laundrDiv.appendChild(this.backupSocialsDiv);


        this.twtBackupDiv = document.createElement('div');
        this.twtBackupDiv.innerHTML = "Twitter";
        this.twtBackupDiv.classList.add("backupbird");
        this.twtBackupDiv.style.display = "inline-block";
        this.twtBackupDiv.style.verticalAlign = "top";
        this.twtBackupDiv.style.minWidth = "40%";
        this.twtBackupDiv.style.color = "blue";
        this.twtBackupDiv.style.textDecoration = "underline";
        this.twtBackupDiv.style.fontSize = "2vw";
        this.backupSocialsDiv.appendChild(this.twtBackupDiv);
        this.twtBackupDiv.style.cursor = 'pointer';
